mapper.select:

select *
from users
where userId=#{userId} and userPwd=#{userPwd}


select userPwd
from users
where userName=#{userName} and userId=#{userId} and userPhone=#{userPhone}

mapper.update:

update users
set userPwd = #{userPwd}, userAddress = #{userAddress}, userName = #{userName}, userPhone = #{userPhone}
where userId=#{userId}
